import React ,{ useEffect } from 'react';
import MyStyle from '../../styles/style';

import { useDispatchContext, useDivisionState, getDivision } from '../../contexts/divisionContext';
import { useLoginState } from '../../contexts/loginContext';
const Divisions = () => {

    const divisionState = useDivisionState();
    const dispatch = useDispatchContext();
    const tokenState = useLoginState()

    useEffect(() => {
        getDivision(dispatch, tokenState.login.token );
    },[])


    const handleClick = (ev) => {
        ev.preventDefault();
        console.log("click sur le boutton")
    }

    return (
        <div className="main">
            <h1 style={MyStyle.titreListDivisions}> Divisions</h1>
            <p style={ MyStyle.textMuted }> Lists de tous les divisions existantes à la radio lomé </p>
            <p style={ MyStyle.textMuted }> vous pouvez rajouté ou modifié , ainsi que les supprimés</p>

            <div style={{ textAlign: "center" , padding: "1.5rem"}}>
                <button className="button-addDivision" onClick={handleClick}>
                    <span style={{ color: "green", fontSize: "1.5rem" , padding:" 0 0.5rem" }}>+</span> 
                Ajouté division </button>
            </div>
            <div>
                    <div className="divisions-container">
                        { dividivisionState.divisions.map( division => {
                           return <div className="divisions" key={division.id}>
                                    <p className="divisions-items id">{division.id} </p>
                                    <p className="divisions-items nom">{ division.nom}</p>
                                </div>
                        })}
                    </div>
            </div>
        </div>
    )
}


export default Divisions;
