import React from "react";
import './Login.css';

const Login = () => {

    const emailRef = createRef();
    const passwordRef = createRef();
    
   
    const handleClick = async (ev) => {

        ev.preventDefault();
       const email = emailRef.current.value;
       const password = passwordRef.current.value;

       const requestBody = {
        query: `
            mutation{
                createDivision(input: { nom: "${email}" }){ id, nom }
            }
        ` 
         }


       if(email.trim().length === 0 || password.trim().length === 0)
       {
          return console.log('Veuillez remplire tous les champs ...');
       }

       const response = await fetch("http://localhost:2000/graphql", {
           method: 'POST',
           body: JSON.stringify(requestBody),
           headers: {
               'Content-Type': 'application/json',
           }
       })     

    }

        return (
            <div className="container-login">
               <div className="login">
                   <form className="form" onSubmit={handleClick}>
                    <h2 className="titre">Authentification </h2>
                       <div className="form-group">
                           <input className="form-controle" type="text" placeholder="votre nom" ref={emailRef} />
                       </div>
                       <div className="form-group">
                           <input className="form-controle" type="password" placeholder="votre mot de pass" ref={passwordRef} />
                       </div>
                       <div className="form-group">
                            <button type="submit" > Login </button>
                       </div>
                   </form>
               </div>
            </div>
        )
    }

export default Login
