import React ,{ useEffect } from 'react';
import MyStyle from '../../styles/style';

import { useDispatchContext, useDivisionState, DivisionProvider } from '../../contexts/divisionContext';

const Divisions = (props) => {

    const divisionState = useDivisionState();
    const dispatch = useDispatchContext();

    useEffect(() => {

        const fetchData = async () => {
            const RequestAddDivision = {
                query: `
                    query{
                       divisions{ id, nom }
                    }
                `
            }
        
            const result = await fetch('http://localhost:8000/graphql',{
                method: "post",
                body: JSON.stringify(RequestAddDivision),
                headers: {
                    "content-type" : "application/json"
                }
            })

            if( result.status !== 200 && result.status !== 201)
                throw new Error(" error de fetch DIVISION datas ERROR STUTS: " + result.status)

            const {data} = await result.json();
            console.log(data.divisions)
            dispatch({ type: "GET_DIVISION " , payload: data.divisions })
        }
        fetchData()
    },[])
    return (
        <div className="main">
            { divisionState }
            <h1 style={MyStyle.titreListDivisions}> Divisions</h1>
            <p style={ MyStyle.textMuted }> Lists de tous les divisions existantes à la radio lomé </p>
            <p style={ MyStyle.textMuted }> vous pouvez rajouté ou modifié , ainsi que les supprimés</p>

            <div style={{ textAlign: "center" , padding: "1.5rem"}}>
                <button className="button-addDivision">
                    <span style={{ color: "green", fontSize: "1.5rem" , padding:" 0 0.5rem" }}>+</span> 
                Ajouté division </button>
            </div>
            <div>
            </div>
        </div>
    )
}


export default Divisions;
