import React from 'react';
import { useLoginState, useLoginDispatchState,login } from '../../contexts/loginContext';

const Login = (props) => {
    const state = useLoginState();
    const dispatch = useLoginDispatchState();

    const emailRef = React.createRef();
    const passwordRef = React.createRef();

    const handleSubmit =( ev ) => {
        ev.preventDefault();
        
        const email = emailRef.current.value.trim();
        const password = passwordRef.current.value.trim();

        if(email.length === '' || password.length === '')
            throw new Error('le email ou le password ne doit pas etre vide ');
        const myLogin = { email, password }
        login(dispatch ,myState )
    }

    return (
        <div className="login-container">
            { console.log(state )}
            <div className="login">
                <h1> Login page </h1>
                <form className="form" onSubmit={handleSubmit}>
                    <div className="form-input">
                        <label className="label" htmlFor="email"> Email </label>
                        <input type="email" ref={emailRef}  id="email" className="form-control" placeholder="Entrez votre email " required />
                    </div>
                    <div className="form-input">
                        <label className="label" htmlFor="password"> Password </label>
                        <input type="password"  ref={passwordRef} id="password" className="form-control" placeholder="Entrez votre password " required />
                    </div>
                    <div className="form-group">
                        <button type="submit" className="login-btn"> Login </button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Login;
