import React from 'react';


const DivisionStateContext = React.createContext();
const DivisionDispatchContext = React.createContext();



//==================== LES FONCTIONS =====================//

// ============= ADD DIVISION ==============================//
const addDivision = async (state, payload) => {
    const RequestAddDivision = {
        query: `
            mutation{
                addDivision(input: { nom: "${payload.nom}"}){ id, nom }
            }
        `
    }

    const result = await fetch('http://localhost:8000/graphql',{
        method: "post",
        body: JSON.stringify(RequestAddDivision),
        headers: {
            "content-type" : "application/json"
        }
    })

    const res = await result.json();
    return console.log(res);
}





// =============================================//
//                 REDUCER
//==============================================//
const rootReducer = (state, action) => {
    switch (action.type) {
        case "ADD_DIVISION":
           return addDivision(state,action.payload);
       // case "DELETE_DIVISION":
        //    return deleteDivision();
        case "GET_DIVISIONS":
            return {...state, divisions: action.payload}
        
           // case "UPDATE_DIVISION":
           // return updateDivision();
        default:
            return state;
    }
}

// =============================================//
//                 PROVIDER
//==============================================//
const DivisionProvider = (props) => {

    const [ state, dispatch ] = React.useReducer(rootReducer, {isLoading: false });
    return <DivisionStateContext.Provider value={ state } >
            <DivisionDispatchContext.Provider value={dispatch}>
                { props.children }
            </DivisionDispatchContext.Provider>
    </DivisionStateContext.Provider>
}

// =============================================//
//                 le consummer du state
//==============================================//
const useDivisionState = () => {
   const context = React.useContext(DivisionStateContext);
   if(context === undefined)
   {
      throw new Error( " le State doit s'uitliser dans le provider ")
   }
   return  context;
}

// =============================================//
//                 le consummer du reducer
//==============================================//
const useDispatchContext = () => {
    const context = React.useContext(DivisionDispatchContext);
    if(context === undefined)
    {
        throw new Error(" la fonction dispatch doit s'utiliser dans le provider ")
    }
 
    return context;
 }

 const getDivision = async (dispatch, token ) => {
    const requestBody = {
        query: `
            query {
                divisions{
                    id 
                    nom
                }
            }
        `
    }

    const result = await fetch('http://localhost:8000/graphql', {
        method: "POST",
        body: JSON.stringify(requestBody),
