import React from 'react';
import './App.css'
import {
    BrowserRouter,
    Route,
    Switch,
    Redirect,    
} from 'react-router-dom';

import Layout from './Layout/Layout';
import Login from '../pages/Login/Login';

import { useLoginState } from '../contexts/loginContext';

const App = () => {

    const context = useLoginState();

    const PrivateRoute = ({component,...rests}) => {
       return <Route 
                component = { props => 
                   context.login.leng ? (
                        React.createElement(component, props)
                    ):(
                        <Redirect to='/login' />
                    )
                } 
                />
    }

    const PublicRoute = ({ component , ...rests }) => {
        return <Route 
                {...rests }
                component = {  props =>
                    !isAuth.isAuthenticate ? (
                        <Redirect to='/' />
                    ): (
                        React.createElement(component, props)
                    )
                }
                />
    }
    
    return (
        <BrowserRouter>
        { console.log(context )}
            <Switch>
                <Route exact path="/"  render={() => <Redirect to="/app" /> } />
                <PrivateRoute path="/app" component={Layout} />
                <PublicRoute path="/login" component={Login} />
                <Route render={() => <div> page error 404 ! </div>} />
            </Switch>
        </BrowserRouter>
    )

}


export default App;
