import React from 'react';
import { Link} from 'react-router-dom';

const FilesContents = () => {
    const [errors, setError ] = React.useState([]);
    const [loading, setLoading ] = React.useState(false);
    const fetchData = async () => {
       try {
           setLoading(true);
            const result = await fetch("http://localhost:9001/dossier/",{
                method: "GET",
                headers: {
                    'Content-Type': 'application/json'
                }
            })

            const data = await result.json();
            setTimeout(function(){setLoading(false);}, 3000)
            setError([]);
            console.log(errors);
            return console.log(data);
       } catch (error) {
           console.log(error);
           setLoading(false);
           return setError(error);
       }
    }
    React.useEffect(function(){
        fetchData();
    },[])

    return (
        <React.Fragment>
        <h3 className="font-weight-bold">Tous les fichiers </h3>
            {loading ? (<h3>loading....</h3>):(
                <div className="d-flex flex-wrap justify-content-between">
                <div style={{ width:"48%"}} className="border p-2 mb-2" >
                    <p className="text-primary">nom fichier 1</p>
                    <p className="text-muted">Description du fichier 1</p>
                    <div className="d-flex justify-content-between">
                        <p className="text-danger">mp4</p>
                        <p className="text-muted">created at: 12-03-2020</p>
                    </div>
                </div>
               
                <div style={{ width:"48%"}} className="border mb-2 rounded p-2" >
                    <p className="text-info d-flex font-weight-bold justify-content-between">nom fichier 2 
                        <span className="text-danger text-right">
                            <i className="fa fa-trash" onClick={function(){ console.log("clique sur le bouton supprimer")}} aria-hidden="true"></i>
                        </span> 
                    </p>
                    <p className="text-muted">Description du fichier 3</p>
                    <div className="d-flex justify-content-between align-items-center flex-wrap">
                        <p className="text-success"><span style={{height: "2px" , width:"2px"}} className="rounded-circle bg-primary"></span> mp3</p>
                        <p className="text-muted">created at: 17-03-2020</p>
                        <Link className="text-center p-1 bg-light border rounded text-primary" to="/dashboard/4">
                        <i className="fa fa-eye p-1" aria-hidden="true"></i>   
                         voire détails</Link>
                    </div>
                </div>
               
                <div style={{ width:"48%"}} className="border mb-2 rounded p-2" >
                    <p className="text-primary">nom fichier 3</p>
                    <p className="text-muted">image jpg </p>
                    <div className="d-flex justify-content-between">
                        <p className="text-success">jpg</p>
                        <p className="text-muted">created at: 12-03-2020</p>
                    </div>
                </div>
               
                <div style={{ width:"48%"}} className="border rounded p-2" >
                    <p className="text-info">nom fichier 4</p>
                    <p className="text-muted">Description du fichier 4</p>
                    <div className="d-flex justify-content-between align-items-center flex-wrap">
                        <p className="text-success"><span style={{height: "2px" , width:"2px"}} className="rounded-circle bg-primary"></span> mp3</p>
                        <p className="text-muted">created at: 17-03-2020</p>
                        <Link className="text-center p-1 bg-light border rounded text-primary" to="#details">
                        <i className="fa fa-eye p-1" aria-hidden="true"></i>   
                         voire détails</Link>
                    </div>
                </div>
            </div>
            )}
        </React.Fragment>

    )
}

export default FilesContents;
