import React from "react";
import Navbar from "../component/Navbar";


export default function(prop) {
const [files , setFiles ] = React.useState([{id: 1, nom: "application.exe", chemin: "C:/application"}])
const [loading, isLoading] = React.useState(false);

React.useEffect( function() {
      function fetchData(){
            isLoading(true);
            console.log("les données sont entrain de ce charger");
      }
      fetchData();
      setTimeout(function(){
            isLoading(false)
      } , 2000)

},[])

return (
    <div className="container">
          <Navbar />
          <div className="row">
                  <h2 className="display-4 text-center p-3 font-weight-bold">
                        Listes des fichiers
                  </h2>
            <div className="col-md-8 mt-3">
            <div className="card">
                  <div className="card-header bg-primary d-flex justify-content-center">
                        <h2 className="font-weight-bold">
                            Fichiers  
                        </h2>
                  </div>
                  <div className="card-body">
                  
                  {loading ? (<h4>Is loading......</h4>): (<table className="table table-hover">
                        <thead>
                              <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">nom</th>
                                    <th scope="col">Action</th>
                              </tr>
                        </thead>
                        <tbody>
                              { files.length ? files.map(file =>{
                              return (
                                    <tr key={file.id}>
                                          <td>{file.id}</td>
                                          <td>{file.nom}</td>
                                          <td>Télécharger</td>
                                    </tr>
                                    )
                              }): 
                              (<h4 className="text-danger">Pas de fichiers </h4>)}
                        </tbody>
                  </table>)}
                  </div>
                  </div>
            </div>
            <div className="col-md-4 mt-3">
                  <div className="border d-flex flex-column justify-content-center p-2">
                        <h3 className="text-success font-weight-bold">Upload</h3>
                        <p className="text-muted">Envoyé tous vos fichiers sur cet cloud</p>
                  </div>
            </div>
            
          </div>
    </div>
)

}
