import React from 'react';
import { Link} from 'react-router-dom';

const FilesContents = () => {
    const [errors, setError ] = React.useState([]);
    const [loading, setLoading ] = React.useState(false);
    const [folders, setFolders] = React.useState([]);
    const fetchData = async () => {
       try {
           setLoading(true);
            const result = await fetch("http://localhost:9001/dossier/",{
                method: "GET",
                headers: {
                    'Content-Type': 'application/json'
                }
            })

            const data = await result.json();
            setTimeout(function(){setLoading(false);}, 1000);
            setError([]);
            console.log(errors);
            return setFolders(data.folders);
       } catch (error) {
           console.log(error);
           setLoading(false);
           return setError(error);
       }
    }
    React.useEffect(function(){
        fetchData();
    },[])

    return (
        <React.Fragment>
            <h3 className="font-weight-bold">Tous les fichiers </h3>
            {loading ? (<h3>loading....</h3>):(
                <div className="d-flex flex-wrap justify-content-between">
                    {folders.length > 0 ? folders.map(function(fold){
                    return <div key={fold.nom} style={{ width:"48%"}} className="border p-2 mb-2" >
                                <img className="img-circle" alt="image" />
                                <p className="text-primary text-center">{fold.nom}</p>
                                <p className="text-muted">Dossier {fold.nom} contenant tous les fichiers du type {fold.type} </p>
                                <div className="d-flex justify-content-between align-items-center flex-wrap">
                                    <p className="text-success"><span style={{height: "2px" , width:"2px"}} className="rounded-circle bg-primary"></span> mp3</p>
                                    <p className="text-muted">created at: 17-03-2020</p>
                                    <Link className="text-center p-1 bg-light border rounded text-primary" to="#details">
                                    <i className="fa fa-eye p-1" aria-hidden="true"></i>   
                                    voire d√©tails</Link>
                                </div>
                            </div>
                    }): (<h3 className="text-danger"> La Listes est vide </h3>)}
                </div>
               
            )}
        </React.Fragment>

    )
}

export default FilesContents;
