import React from "react";

import Navbar from "../component/Navbar";
import Footer from "../component/Footer";
import UploadModal from "../component/UploadModal";


export default function(prop) {
const [files , setFiles ] = React.useState([{id: 1, nom: "application.exe", chemin: "C:/application"}])
const [loading, isLoading] = React.useState(false);

React.useEffect( function() {
      function fetchData(){
            isLoading(true);
            console.log("les donnÃ©es sont entrain de ce charger");
      }
      fetchData();
      setTimeout(function(){
            isLoading(false)
      } , 2000)

},[])

return (
    <div>
      <UploadModal />
      <Navbar />
      <div className="container mt-4">
          <div className="row">
                  <h2 className="display-4 text-center p-3 font-weight-bold">
                        Listes des fichiers du cloud
                  </h2>
            <div className="col-md-8 mt-3">
            <div className="card p-none">
                  <div className="card-header bg-info d-flex justify-content-center">
                        <h2 className="font-weight-bold">
                            Fichiers  
                        </h2>
                  </div>
                  <div className="card-body">
                  
                  {loading ? (<h4>Is loading......</h4>): (<table className="table table-hover">
                        <thead>
                              <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">nom</th>
                                    <th scope="col">Action</th>
                              </tr>
                        </thead>
                        <tbody>
                              { files.length ? files.map(file =>{
                              return (
                                    <tr key={file.id}>
                                          <td>{file.id}</td>
                                          <td>{file.nom}</td>
                                          <td>TÃ©lÃ©charger</td>
                                    </tr>
                                    )
                              }): 
                              (<h4 className="text-danger">Pas de fichiers </h4>)}
                        </tbody>
                  </table>)}
                  </div>
                  </div>
            </div>
            <div className="col-md-4 mt-3 ">
                  <div className="border d-flex flex-column align-items-center p-3" style={{background: "#fff"}}>
                        <h3 className="text-success font-weight-bold">Uploads</h3>
                        <p className="text-muted">Envoyez tous vos fichiers Ã  travers cet cloud</p>
                        <p>Cet cloud vous permet d'envoyÃ© n'importe qu'elle taille de fichiers</p>
                        <button 
                              data-toggle="modal"
                              className="btn btn-success" 
                              onClick={handleClick}
                        >EnvoyÃ© un fichier</button>

                  </div>
            </div>
            
          </div>
    </div>
          <Footer />
    </div>
)

}
