import React from 'react';

const loginStateContext = React.createContext();
const loginDispatchContext = React.createContext();



// =============================================//
//                 Root Reducer
//==============================================//
const rootReducer = (state, action) => {
    switch(action.type){
        case "LOGIN":
            return {...state , login: action.payload}
        case "LOGOUT":
            return {...state , login: action.payload}
        default:
            console.log("ce type d'action n'est pas reconnu . veuillez entrez une action correcte : ACTION ERROR: " + action.type );
            return state;
    }
}

// =============================================//
//                 PROVIDER
//==============================================//
const LoginProvider = ({children}) => {
    const [state, dispatch] = React.useReducer(rootReducer, {
        login:{}
    })

    return <loginStateContext.Provider value={state}>
            <loginDispatchContext.Provider value={dispatch}>
                {children}
            </loginDispatchContext.Provider>
    </loginStateContext.Provider>
}
// =============================================//
//                 useLoginState
//==============================================//
const useLoginState = () => {
    // recuperation de notre state grace au consumer
    const context = React.useContext(loginStateContext);
    if(context === undefined)
        throw new Error("le State doit s'utiliser dans un provider")

    return context;
}

// =============================================//
//                 useLoginDispatchState
//==============================================//
const useLoginDispatchState = () => {
    // recuperation de notre fonction dispatch grace au consumer
    const context = React.useContext(loginDispatchContext);
    if(context === undefined)
        throw new Error(" la fonction dispatch doit s'utilsier dans le provider")
    return context;
}

// =============================================//
//                 Order fonctions
//==============================================//
// LOGIN FONCTION IMPLEMENTATION
const login = async (dispatch, myLoginState) => {
    const requestBody = {
        query: `
            query{
                login(email: "${myLoginState.email }", password: "${myLoginState.password}"){
                     userId 
                     userEmail 
                     admin 
                     token 
                     tokenExpiration
                 }
            }
        `
    }

    const results = await fetch('http://localhost:8000/graphql', {
        method: "POST",
        body: JSON.stringify(requestBody),
        headers:{
            'Content-type': 'application/json'
        }
    })
    if(results.status !==  201 && results.status !== 200){
        console.log(results.json())
        throw new Error("erreur de Login ..." + results.statusText);
    }
    const {data} =  await results.json();

    dispatch({type: "LOGIN" , payload: data.login })
}

const logout = (dispatch) => {
    dispatch({type: "LOGOUT " , payload: { token: null, userId: null, userEmail: '', tokenExpiration: ''}});
}
// =============================================//
//                MODULES iMPORTS               //

export {
    useLoginDispatchState,
    useLoginState,
    LoginProvider,
    login,
    logout
}
