import React from "react";
import Navbar from "../component/Navbar";


export default function(prop) {
const [files , setFiles ] = React.useState([{id: 1, nom: "application.exe", chemin: "C:/application"}])
const [loading, isLoading] = React.useState(false);

React.useEffect( function() {
      function fetchData(){
            isLoading(true);
            console.log("les données sont entrain de ce charger");
      }
      fetchData();
      setTimeout(function(){
            isLoading(false)
      } , 2000)

},[])

return (
    <div className="container">
          <Navbar />
          <div className="row">
            <div className="col-md-7">
            {loading ? (<h4>Is loading......</h4>): (<table className="table table-hover">
                  <thead>
                        <tr>
                              <th scope="col">#</th>
                              <th scope="col">nom</th>
                              <th scope="col">Action</th>
                        </tr>
                  </thead>
                  <tbody>
                        { files.length ? files.map(file =>{
                        return (
                              <tr key={file.id}>
                                    <td>{file.id}</td>
                                    <td>{file.nom}</td>
                                    <td>Télécharger</td>
                              </tr>
                              )
                        }): 
                        (<h4 className="text-danger">Pas de fichiers </h4>)}
                  </tbody>
            </table>)}
            </div>
            <div className="col-md-5">
                  <form className="form">

                  </form>
            </div>
            
          </div>
    </div>
)

}
