import {LOGIN} from '../types/types';

// les fonctions 

const login = async (state, payload) => {

    try{

        const queryBody = {
            query: `
                query{
                    login(email: "${payload.email}" , password: "${payload.password}") { userId , token , tokenExpiration }
                }
            `
        }
    
        const res = await fetch('http://localhost:8000/graphql',
        {
            method: 'POST',
            body: JSON.stringify(queryBody),
            headers: {
                'Content-Type': 'application/json'
            }
        });
        
        if(res.status !== 200 && res.status !== 201)
            throw new Error('Erreur de recupération des informations de la base de donné');
        
        const {data} = await res.json();
        const newState = {...state, login:data.login}
       return newState;
    }catch(err){
        throw new Error(err);
    }
  
}


// le reducer
const reducers = (state, action) => {
    switch(action.type ){
        case LOGIN:
            return login(state, action.auth);
        default:
            return state;
    }

}
export default reducers;
