import React from 'react';

export default function(prop) {

const fromEmailRef = React.createRef();
const toEmailRef = React.createRef();
const fileRef = React.createRef();

const [error, setError] =  React.useState("");
const handleClick = () => {
    // const to = toEmailRef.current.value;
    // const from = fromEmailRef.current.value;
    const filePath = fileRef.current.value;
    
    if(!filePath){
        setError("Vous devez selectionnez le fichier Ã  uploader avant de cliquez sur uploder")
        return setTimeout(function(){setError("")}, 3000)
    }
    
    if(error === ""){
        let xhr = new XMLHttpRequest();
        let form = document.getElementById("myForm");
        let formData = new FormData(form);
        xhr.open("post", "http://localhost:9001/upload");

        xhr.onreadystatechange = function(ev){
            if(xhr.readyState === 4){
                if(xhr.status === 200){
                    prop.isLoading(xhr.responseText);
                }
            }
        }

        xhr.upload.addEventListener('progress', (data) => {
            let progressBar = document.querySelector('.progress-bar');
            const myCloseButton = document.getElementById("myCloseButton");
            const total = data.total;
            let progress = Math.floor((data.loaded *100)/total);

            progressBar.style = "width:"+progress+"%"
            progressBar.textContent = progress + "%"
            if( total === data.loaded ){
                myCloseButton.click()
                progressBar.style = "width: 0%"
                progressBar.textContent = "0%"
            }
        })
        

        return xhr.send(formData);
    }
    
}

return (
    <div className="modal fade" data-keyboard="true" data-backdrop="static" id="kratosModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
    <div className="modal-dialog modal-dialog-centered" role="document">
        <div className="modal-content">
        <div className="modal-header">
            <h5 className="modal-title" id="exampleModalCenterTitle">UploadÃ©</h5>
            <button type="button" id="myCloseButton" className="close" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">&times;</span>
            </button>
        </div>
        <div className="modal-body">
        {error && <div className="alert alert-danger" role="alert">{error}</div>}
            <form>
                <div>
                    <input id="input-b1" name="input-b1" type="file" className="file" data-browse-on-zone-click="true" multiple />
                </div>
            </form>
            <div className="progress" style={{ height: "35px"}}>
                <div className="progress-bar" role="progressbar" style={{width: 0}}  aria-valuenow="30" aria-valuemin="0" aria-valuemax="100">0%</div>
            </div>
        </div>
        <div className="modal-footer">
            <button type="button" className="btn btn-danger" data-dismiss="modal">FermÃ©</button>
            <button 
                type="button" 
                className="btn btn-outline-primary" 
                onClick={handleClick}
            ><i className="fa fa-upload p-1" aria-hidden="true"></i>EnvoyÃ©</button>
        </div>
        </div>
    </div>
</div>
)

}
