import React ,{ useContext } from "react";
import './Login.css';

import  { AuthContext } from '../../contexts/AuthContext';

const Login = () => {

    const emailRef = React.createRef();
    const passwordRef = React.createRef();
    
    const {Auth, dispatch } = useContext(AuthContext);

    const handleSubmit = (ev) => {
        ev.preventDefault();
        
        const email = emailRef.current.value.trim();
        const password = passwordRef.current.value.trim();

        if(email.length === 0 || password.length === 0){
            return console.log('vous devez remplire tous les champs');
        }

        return dispatch({
            type: 'LOGIN',
            auth:{email,password}
        });
    }
    

        return (
                <div className="container-login">
                    {Auth.email}
                <div className="login">
                    <form className="form" onSubmit={ handleSubmit }>
                        <h2 className="titre">Authentification </h2>
                        <div className="form-group">
                            <input className="form-controle" required type="email" placeholder="votre Mail" ref={emailRef} />
                        </div>
                        <div className="form-group">
                            <input className="form-controle" required type="password" placeholder="votre mot de pass" ref={passwordRef} />
                        </div>
                        <div className="form-group">
                                <button type="submit" > Login </button>
                        </div>
                    </form>
                </div>
                </div>
            
        )
    }

export default Login
