import {LOGIN} from '../types/types';

// les fonctions 
const login = (state, payload)=> {
    const newStat = [...state,{email:payload.email,password:payload.pa}]
}


// le reducer
const reducers = (state, action) => {
    switch(action.type ){
        case LOGIN:
            return login(state, action.auth);
        default:
            return state;
    }
}
export default reducers;
