import React from 'react';
import {
    HashRouter,
    Route,
    Switch,
    Redirect    
} from 'react-router-dom';

import Layout from './Layout/Layout';
import Login from '../pages/Login/Login';


const App = () => {

    const [isAuth, setIsAuth ] = React.useState({isAuthenticate: true });

    const PrivateRoute = ({component,...rests}) => {
        return <Route 
                   component ={ isAuth.isAuthenticate ? (
                        React.createElement(component , props )
                    ):
                    (
                        () => <Redirect to='/login' />
                    ) }  />
    }
    
    return (
        <HashRouter>
            <Switch>
                <Route exact path="/"  component={() => <Redirect to="/app/dashboard" /> } />
                <Route exact path="/app" component={() => <Redirect to="/app/dashboard" /> } />
                <Route path="/app" component={Layout} />
                <Route path="/login" component={Login} />
            </Switch>
        </HashRouter>
    )

}


export default App;
