import React ,{ useContext } from "react";
import './Login.css';

import {AuthContext} from '../../contexts/AuthContext';

const Login = () => {

    const { dispatch } = useContext(AuthContext);

    const handleSubmit = (ev) => {
        ev.preventDefault();
        
        dispatch({
            type: 'LOGIN',
            auth: { email: 'kratos', password: 'bosse'}
        })

        return console.log()
    }

        return ( 
                <div className="container-login">
                <div className="login">
                    <form className="form" onSubmit={ handleSubmit }>
                        <h2 className="titre">Authentification </h2>
                        <div className="form-group">
                            <input className="form-controle" type="email" placeholder="votre Mail" />
                        </div>
                        <div className="form-group">
                            <input className="form-controle" type="password" placeholder="votre mot de pass" />
                        </div>
                        <div className="form-group">
                                <button type="submit" > Login </button>
                        </div>
                    </form>
                </div>
                </div>
            
        )
    }

export default Login
