import {LOGIN} from '../types/types';

// les fonctions 
const login = async (state, payload) => {
    const res = await fetch('http://localhost:8000/graphql',
    {
        method: 'POST',
        body: JSON.stringify(queryBody),
        headers: { }
    });

    console.log('la valeur convertis par la fonction json: ' + JSON.stringify(queryBody));
    return {...state,email:payload.email, password: payload.password}
}


// le reducer
const reducers = (state, action) => {
    switch(action.type ){
        case LOGIN:
            return login(state, action.auth);
        default:
            return state;
    }

}
export default reducers;
