import React from 'react';
import { 
    getEmployes , 
    useEmployeContext,
    useEmployeDispatchContext
  } from '../../contexts/employeContext';

import { useLoginState } from '../../contexts/loginContext';

const Employe = () => {

    const dispatch = useEmployeDispatchContext(); 
    const state = useEmployeContext();
    const tokenState = useLoginState();

    React.useEffect(() => {
        console.log(to)
        getEmployes(dispatch,tokenState.login.token);
    },[])

    return (
        <div className="main">
            <h1 className="title"> Listes de tous les employ√©s</h1>
           <div className="card-container">
             { state.employes.length ? state.employes.map( employe => (
                 <div className="card" key={employe.id }>
                    <div className="card-header">
                        <h1 className="card-title">{ employe.nom } { employe.prenom } </h1>
                    </div>
                    <div className="card-body">
                        <p> { employe.situation_matrimoniale} </p>
                        <p> { employe.sexe}</p>
                        <p> { employe.address.pays}</p>
                        <p>{ employe.address.ville}</p>
                        <p> { employe.division.nom} </p>
                    </div>
                    <div className="card-footer">
                        <button className="description-employe-button">Description </button>
                        <button className="supprimer-employe-button"> Supprimer </button>
                    </div>
                 </div>
             )): ( <h2 style={{ color: "red"}}> la list est vide </h2>)}
             <div>
                 odij
             </div>
           </div>
        </div>
    )
}


export default Employe;
