import React from 'react';

export default function(prop) {

const fromEmailRef = React.createRef();
const toEmailRef = React.createRef();
const fileRef = React.createRef();

const [error, setError] =  React.useState("");
const handleClick = () => {
    // const to = toEmailRef.current.value;
    // const from = fromEmailRef.current.value;
    const filePath = fileRef.current.value;
    
    if(!filePath){
        setError("Vous devez selectionnez le fichier à uploader avant de cliquez sur uploder")
        return setTimeout(function(){setError("")}, 3000)
    }
    
    if(error === ""){
        let xhr = new XMLHttpRequest();
        let form = document.getElementById("myForm");
        let formData = new FormData(form);
        xhr.open("post", "http://localhost:9001/upload");
        
        console.log(xhr.upload);
        xhr.upload.addEventListener('progress', (data) => {
            let progressBar = document.querySelector('.progress-bar');
            const total = data.total;
            let progress = Math.rou(data.loaded *100)/total;
            progressBar.style = "width:"+progress+"%"
            progressBar.textContent = progress + "%"
            if( progress === data.loaded )
                console.log("upload fini") 
        })

        return xhr.send(formData)
    }
    
}

return (
    <div className="modal fade" data-keyboard="true" data-backdrop="static" id="kratosModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
    <div className="modal-dialog modal-dialog-centered" role="document">
        <div className="modal-content">
        <div className="modal-header">
            <h5 className="modal-title" id="exampleModalCenterTitle">Uploadé</h5>
            <button type="button" id="myCloseButton" className="close" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">&times;</span>
            </button>
        </div>
        <div className="modal-body">
        {error && <div className="alert alert-danger" role="alert">{error}</div>}
            <form id="myForm" encType="multipart/form-data">
                <div className="form-group">
                    <label htmlFor="from">Email de l'envoyeur (Optionnel)</label>
                    <div className="input-group mb-2">
                        <div className="input-group-prepend">
                            <div className="input-group-text">@</div>
                        </div>
                        <input ref={fromEmailRef} type="mail" className="form-control" id="from" placeholder="Votre Email" />
                    </div>
                </div>
                <div className="form-group">
                    <label htmlFor="to">Email du destinataire (Optionnel)</label>
                    <div className="input-group mb-2">
                        <div className="input-group-prepend">
                            <div className="input-group-text">@</div>
                        </div>
                        <input ref={toEmailRef} type="mail" className="form-control" id="tp" placeholder="Destinataire Email " />
                    </div>
                </div>
                <div className="form-group">
                        <input ref={fileRef} type="file" required className="form-control" name="myFile" id="fichier" placeholder="Choisir un fichier *" />
                </div>
            </form>
            <div className="progress" style={{ height: "30px"}}>
                <div className="progress-bar" role="progressbar" style={{width: 0}}  aria-valuenow="25" aria-valuemin="0" aria-valuemax="100"></div>
            </div>
        </div>
        <div className="modal-footer">
            <button type="button" className="btn btn-secondary" data-dismiss="modal">Fermé</button>
            <button 
                type="button" 
                className="btn btn-primary" 
                onClick={handleClick}
            >Envoyé</button>
        </div>
        </div>
    </div>
</div>
)

}
